// Session Aggregator - Collects audio chunks by session
const data = $input.first().json;
const sessionId = data.recordingSessionId;

// Get or initialize static data for session storage
const staticData = $getWorkflowStaticData('global');
if (!staticData.sessions) {
  staticData.sessions = {};
}

// Initialize session if first chunk
if (data.isFirstChunk) {
  staticData.sessions[sessionId] = {
    sessionId: sessionId,
    meetingId: data.meetingId,
    meetingUrl: data.meetingUrl,
    title: data.title,
    source: data.source,
    recordingType: data.recordingType,
    startTime: data.timestamp,
    chunks: [],
    isComplete: false
  };
  console.log(`[SESSION] Started: ${sessionId}`);
}

// Add chunk to session
if (staticData.sessions[sessionId]) {
  staticData.sessions[sessionId].chunks.push({
    chunkIndex: data.chunkIndex,
    audio: data.audio,
    timestamp: data.timestamp,
    duration: data.duration
  });

  console.log(`[SESSION] Added chunk ${data.chunkIndex} to ${sessionId}`);

  // Check if session is complete
  if (data.isLastChunk) {
    staticData.sessions[sessionId].isComplete = true;
    staticData.sessions[sessionId].endTime = data.timestamp;

    // Calculate total duration
    const totalDuration = staticData.sessions[sessionId].chunks.reduce(
      (sum, chunk) => sum + chunk.duration, 0
    );
    staticData.sessions[sessionId].totalDuration = totalDuration;

    console.log(`[SESSION] Completed: ${sessionId} - ${totalDuration}s`);

    // Return complete session for processing
    const completeSession = { ...staticData.sessions[sessionId] };

    // Clean up completed session
    delete staticData.sessions[sessionId];

    return { ...completeSession };
  }
}

// Return null if session not complete (stops workflow)
return null;

// Prepare audio data for OpenAI transcription
const session = $input.first().json;
const results = [];

// Sort chunks by index
const sortedChunks = session.chunks.sort((a, b) => a.chunkIndex - b.chunkIndex);

// Process each chunk for transcription
for (let i = 0; i < sortedChunks.length; i++) {
  const chunk = sortedChunks[i];

  // Convert base64 audio to binary data
  const audioBuffer = Buffer.from(chunk.audio, 'base64');

  results.push({
    json: {
      sessionId: session.sessionId,
      chunkIndex: chunk.chunkIndex,
      sessionMetadata: {
        meetingId: session.meetingId,
        title: session.title,
        source: session.source,
        recordingType: session.recordingType,
        totalDuration: session.totalDuration,
        totalChunks: sortedChunks.length
      }
    },
    binary: {
      audio: {
        data: audioBuffer,
        mimeType: 'audio/webm',
        fileName: `chunk_${chunk.chunkIndex}.webm`
      }
    }
  });
}

return results;

// Reassemble transcriptions by session
const items = $input.all();
const sessionId = items[0].json.sessionId;
const metadata = items[0].json.sessionMetadata;

// Collect all transcriptions
const transcriptions = items.map(item => ({
  chunkIndex: item.json.chunkIndex || 0,
  text: item.json.text || ''
}));

// Sort by chunk index and combine
transcriptions.sort((a, b) => a.chunkIndex - b.chunkIndex);
const fullTranscription = transcriptions.map(t => t.text).join(' ').trim();

console.log(`[TRANSCRIPTION] Assembled ${transcriptions.length} chunks for session ${sessionId}`);

return {
  sessionId: sessionId,
  fullTranscription: fullTranscription,
  metadata: metadata,
  transcriptionChunks: transcriptions.length
};

// Parse AI analysis and prepare for Notion
const transcriptionData = $node["Reassemble Transcriptions"].json;
const aiResponse = $input.first().json;

let analysis;
try {
  // Clean the response and parse JSON
  const cleanResponse = aiResponse.replace(/```json\s*|\s*```/g, '').trim();
  analysis = JSON.parse(cleanResponse);
} catch (error) {
  console.error('Error parsing AI analysis:', error);
  // Fallback structure
  analysis = {
    executiveSummary: "Analysis parsing failed - raw transcription available",
    mainTopics: [],
    keyDecisions: [],
    actionItems: [],
    participants: [],
    questions: [],
    nextSteps: [],
    meetingType: "unknown",
    priorityLevel: "medium",
    followUpRequired: false,
    tags: []
  };
}

const metadata = transcriptionData.metadata;

// Prepare comprehensive Notion data
const notionData = {
  // Page Properties
  title: metadata.title || `${metadata.source} Recording`,
  recordingDate: new Date().toISOString().split('T')[0],
  platform: metadata.source,
  recordingType: metadata.recordingType,
  duration: `${Math.round(metadata.totalDuration / 60)} minutes`,
  sessionId: transcriptionData.sessionId,

  // Analysis Results
  executiveSummary: analysis.executiveSummary,
  mainTopics: analysis.mainTopics,
  keyDecisions: analysis.keyDecisions,
  actionItems: analysis.actionItems,
  participants: analysis.participants,
  questions: analysis.questions,
  nextSteps: analysis.nextSteps,

  // Classification
  meetingType: analysis.meetingType,
  priorityLevel: analysis.priorityLevel,
  followUpRequired: analysis.followUpRequired,
  tags: [...analysis.tags, metadata.source, metadata.recordingType].filter((tag, index, self) => self.indexOf(tag) === index),

  // Full Content
  fullTranscription: transcriptionData.fullTranscription,

  // Metadata
  originalUrl: metadata.meetingUrl || '',
  createdAt: new Date().toISOString()
};

console.log(`[NOTION] Prepared data for: ${notionData.title}`);

return notionData;
